('nu5fee06b1acbe4bb', 'NUBACKUP', 'Create a Database Backup using mysqldump', 'nubuilder', 'if (nuDemo()) return;\n	\n// *******************************************************************\n\n// Settings. Please modify if different.\n\n//Use nuBuilder DB settings. Path to nuconfig.php\n$path_nuconfig_php = __DIR__ . \'/../nuconfig.php\';\n\n// Path to Mysqldump.php\n$path_mysqldump_php = __DIR__ . \'/libs/mysqldump/Mysqldump.php\';\n\n// Directory to write the sql dump to\n$path_sql_dump = __DIR__ . \'/libs/mysqldump/dumps/\';\n\n//global $nuConfigBackupLocation;\n// $path_sql_dump = (! isset($nuConfigBackupLocation) || $nuConfigBackupLocation = \'\') ?  __DIR__ . \'/libs/mysqldump/dumps/\' : nuStringAddTrailingCharacter($nuConfigBackupLocation);\n\n// Save dump to file:\n$file_name =  date(\'m-d-Y_H:i:s\') . \'_\' . \'nuBuilder_backup\' . \'.sql.gzip\';\n\n// *******************************************************************\n\ntry {\n    require_once ($path_mysqldump_php);\n} catch (Exception $e) {\n    nuDisplayError(\'require_once failed! Error: \'.$e);\n}\n\ntry {\n    require $path_nuconfig_php;\n} catch (Exception $e) {\n    nuDisplayError(\'require failed! Error: \'.$e);\n}\n\n// Dump Settings\n$dumpSettings = array();\n$dumpSettings[\'single-transaction\'] = false;\n$dumpSettings[\'no-create-info\'] = false;\n$dumpSettings[\'lock-tables\'] = false;\n$dumpSettings[\'add-locks\'] = false;\n$dumpSettings[\'extended-insert\'] = false;\n$dumpSettings[\'skip-definer\'] = true;\n$dumpSettings[\'compress\'] = Ifsnop\\Mysqldump\\Mysqldump::GZIP;\n\n// Create Mysqldump\n$dumper = new Ifsnop\\Mysqldump\\Mysqldump(\"mysql:host=$nuConfigDBHost;dbname=$nuConfigDBName\", $nuConfigDBUser, $nuConfigDBPassword, $dumpSettings);\n\nif (\"#nubackup_user_records_only#\"  == \"1\") {\n\n    $tableWheres =  array(\n        \"zzzzsys_access\"        =>  \"zzzzsys_access_id not like \'nu%\' \",\n        \"zzzzsys_access_form\"   =>  \"zzzzsys_access_form_id not like \'nu%\' \",\n        \"zzzzsys_access_php\"    =>  \"zzzzsys_access_php_id not like \'nu%\' \",\n        \"zzzzsys_access_report\" =>  \"zzzzsys_access_report_id not like \'nu%\' \",\n        \"zzzzsys_browse\"        =>  \"zzzzsys_browse_id not like \'nu%\' \",\n        \"zzzzsys_cloner\"        =>  \"zzzzsys_cloner_id not like \'nu%\' \",\n        \"zzzzsys_code_snippet\"  =>  \"zzzzsys_code_snippet_id not like \'nu%\' \",\n        \"zzzzsys_debug\"   		=>  \"zzzzsys_debug_id not like \'nu%\' \",\n        \"zzzzsys_event\"   		=>  \"zzzzsys_event_id not like \'nu%\' \",\n        \"zzzzsys_file\"   		=>  \"zzzzsys_file_id not like \'nu%\' \",\n        \"zzzzsys_form\"   		=>  \"zzzzsys_form_id not like \'nu%\' \",\n        \"zzzzsys_format\"        =>  \"zzzzsys_format_id not like \'nu%\' \",\n        \"zzzzsys_info\"          =>  \"zzzzsys_info_id not like \'nu%\' \",\n        \"zzzzsys_note\"          =>  \"zzzzsys_note_id not like \'nu%\' \",\n        \"zzzzsys_note_category\" =>  \"zzzzsys_note_category_id not like \'nu%\' \",\n        \"zzzzsys_object\"   		=>  \"zzzzsys_object_id not like \'nu%\' \",\n        \"zzzzsys_object_list\"   =>  \"zzzzsys_object_list_id not like \'nu%\' \",\n        \"zzzzsys_php\"   		=>  \"zzzzsys_php_id not like \'nu%\' \",\n        \"zzzzsys_report\"   		=>  \"zzzzsys_report_id not like \'nu%\' \",\n        \"zzzzsys_report_data\"   =>  \"zzzzsys_report_data_id not like \'nu%\' \",\n        \"zzzzsys_run_list\"   	=>  \"zzzzsys_run_list_id not like \'nu%\' \",\n        \"zzzzsys_select\"   		=>  \"zzzzsys_select_id not like \'nu%\' \",\n        \"zzzzsys_select_clause\" =>  \"zzzzsys_select_clause_id not like \'nu%\' \",\n        \"zzzzsys_session\"   	=>  \"zzzzsys_session_id not like \'nu%\' \",\n        \"zzzzsys_setup\"   		=>  \"zzzzsys_setup_id not like \'nu%\' \",\n        \"zzzzsys_tab\"   		=>  \"zzzzsys_tab_id not like \'nu%\' \",\n        \"zzzzsys_timezone\"   	=>  \"zzzzsys_timezone_id not like \'nu%\' \",\n        \"zzzzsys_translate\"   	=>  \"zzzzsys_translate_id not like \'nu%\' \",\n        \"zzzzsys_user\"   		=>  \"zzzzsys_user_id not like \'nu%\' \"\n    \n    );\n    \n    $dumper->setTableWheres($tableWheres);\n\n}\n\n\n$dump_file = $path_sql_dump . sanitizeFilename($file_name);\n\n// Start the dump\ntry {\n    \n    if (!is_dir($path_sql_dump))\n    {\n        mkdir($path_sql_dump, 0755);\n    }\n    \n    $dumper->start($dump_file);\n}\ncatch(\\Exception $e) {\n    nuDisplayError(\'Export Error: \' . $e->getMessage());\n}\n\n$dump_file = base64_encode($path_sql_dump . $file_name);\n\n$js = \"\n   nuMessage([\'<h2>Export completed!</h2><br>SQL Dump saved in \' + atob(\'$dump_file\')]);\n\";\n\nnuJavascriptCallback($js);\n\nfunction sanitizeFilename($file) {\n    $file = mb_ereg_replace(\"([^\\w\\s\\d\\-_~,;\\[\\]\\(\\).])\", \'\', $file);\n    return mb_ereg_replace(\"([\\.]{2,})\", \'\', $file);\n}', 'hide', '', '1', '0', '', NULL),
('nu5ff7efb1ed369a6', 'NUREFRESHSELECTOBJECT', 'Refresh a select object', 'nubuilder', 'function nuGetSelectValues($formId, $selectId) {\n\n    $sql = \"\n        SELECT\n            sob_select_sql\n        FROM\n            `zzzzsys_object`\n        WHERE\n            sob_all_zzzzsys_form_id = ? AND sob_all_id = ?\n    \";\n\n    $t = nuRunQuery($sql, array($formId, $selectId));\n\n    $a = array();\n    if (db_num_rows($t) == 1) {\n\n        $r = db_fetch_row($t);\n        if ($r != false) {\n            \n            $disS = nuReplaceHashVariables($r[0]);\n\n            $t = nuRunQuery($disS);\n\n            while ($row = db_fetch_row($t)) {\n                $a[] = $row;\n            }\n\n            return json_encode($a);\n        }\n\n    }\n\n    return false;\n\n}\n\nfunction nuPopulateSelectObject($formId, $selectId, $removeBlank, $prefix) {\n\n    $j = nuGetSelectValues($formId, $selectId);\n\n    $selectId = $prefix.$selectId;\n\n    $cb = \"if (window.nuSelectObjectRefreshed) {\n    	    nuSelectObjectRefreshed(\'$formId\', \'$selectId\', count);\n         }\";\n\n    if ($j == false) {\n        \n        return \"var count = -1; \".$cb;\n        \n    } else {\n    	\n    return \"\n    	function nuPopulateSelectObject() {\n    	\n    		var p = $j;\n    \n    		$(\'#$selectId\').empty();\n    		\n    		if (\'$removeBlank\' == \'0\' ) {\n    		    $(\'#$selectId\').append(\'<option value=\\\"\\\"></option>\');\n    		}\n            \n            var count = 0;\n    \n    		if (p != \'\') {\n    		    var s = nuIsSaved();\n    			\n    			for (var i = 0; i < p.length; i++) {\n    				$(\'#$selectId\').append(\'<option value=\\\"\' + p[i][0] + \'\\\">\' + p[i][1] + \'</option>\');\n    				count ++;\n    			}\n    			\n    			if (s) { nuHasNotBeenEdited(); }\n    			\n    		}\n    		\n    		return count;\n    	}\n    	\n    	var count = nuPopulateSelectObject();\n\n    \".$cb;\n    }\n\n}\n\nfunction nuRefreshSelectObject($selectId, $formId, $removeBlank, $prefix) {\n\n    if (hashCookieNotSetOrEmpty($formId)) {\n        $formId = \'#form_id#\';\n    }\n\n    $prefix = hashCookieNotSetOrEmpty($prefix) ? \'\' : $prefix;\n\n    $js = nuPopulateSelectObject($formId, $selectId, $removeBlank, $prefix);\n    nuJavascriptCallback($js);\n\n}\n\nnuRefreshSelectObject(\'#NUREFRESHSELECTOBJECT_selectid#\', \'#NUREFRESHSELECTOBJECT_formid#\', \'#NUREFRESHSELECTOBJECT_removeblank#\',\'#NUREFRESHSELECTOBJECT_prefix#\');', 'hide', '', '1', '1', '', NULL),
('nu5ff8249659aef00', 'NUREFRESHDISPLAYOBJECT', 'Refresh a display object', 'nubuilder', 'function nuGetDisplayValue($formId, $obj) {\n\n    $sql = \"SELECT sob_display_sql FROM `zzzzsys_object` WHERE sob_all_zzzzsys_form_id = ? AND sob_all_id = ?\";\n\n    $t = nuRunQuery($sql, array($formId, $obj));\n\n    if (db_num_rows($t) == 1) {\n        $r = db_fetch_row($t);\n        if ($r != false) {\n\n            $disS = nuReplaceHashVariables($r[0]);\n            $disT = nuRunQuery($disS);\n\n            if (db_num_rows($disT) >= 1) {\n                $disR = db_fetch_row($disT);\n                return $disR[0];\n            } else {\n                return \"\";\n            }\n        }\n    }\n\n    return false;\n}\n\nfunction nuRefreshDisplayObject($displayId, $formId, $prefix) {\n\n    if (hashCookieNotSetOrEmpty($formId)) {\n        $formId = \'#form_id#\';\n    }\n\n    $prefix = hashCookieNotSetOrEmpty($prefix) ? \'\' : $prefix;\n	$formId = \'#nurefreshdisplayobject_formid#\';\n\n	if (hashCookieNotSetOrEmpty($formId)) {\n		$formId = \'#form_id#\';\n	}\n    \n	$value = nuGetDisplayValue($formId, $displayId);\n	$displayId = $prefix.$displayId;\n		\n	if ($value === false && $value !== \'\') {\n		$js = \"nuMessage([nuTranslate(\'Failed to refresh the Display Object: \') + \'$displayId\']); \";\n	} else {\n\n		$js = \" \n			var obj = $(\'#$displayId\');\n			var format = obj.attr(\'data-nu-format\');\n			var v = nuFORM.addFormatting(\'$value\', format);\n			obj.val(v).change();\n			\n			if (window.nuDisplayObjectRefreshed) {\n				nuDisplayObjectRefreshed(\'$displayId\', \'$formId\');\n			}\n		\";\n	}\n    \n    nuJavascriptCallback($js);\n\n}\n\nnuRefreshDisplayObject(\'#NUREFRESHDISPLAYOBJECT_displayid#\', \'#NUREFRESHDISPLAYOBJECT_formid#\',\'#NUREFRESHDISPLAYOBJECT_prefix#\');', 'hide', '', '1', '1', '', NULL),
('nu6068bee1b6c63be', 'NURUNADDDBCOLUMN', 'Add a new database column', 'nubuilder', 'if (nuHash() [\'GLOBAL_ACCESS\'] == \'0\') return;\n\n$sql = \'ALTER TABLE `#sob_all_table#` ADD \' . \'#sql_query#\';\n$after = \'#sql_after_column#\';\nif ($after != \'\') {\n    $sql = $sql . \" AFTER \" . $after;\n}\n\nif (preg_match(\'[DELETE |DROP |INSERT |;]\', strtoupper($sql))) {\n    $r = - 2;\n}\nelse {\n    $r = nuRunQuery($sql, array() , true);\n}\n\nif ($r == 0) {\n    $js = \"nuMessage([nuTranslate(\'The column has been created successfully\')]); nuRefreshSelectObject(\'sql_after_column\');\";\n}\nelse if ($r == - 1) {\n    $js = \"nuMessage([nuTranslate(\'An error occured while creating the column\'), nuTranslate(\'Check nuDebug Results for details\')]);\";\n\n}\nelse if ($r == - 2) {\n    $js = \"nuMessage([nuTranslate(\'An error occured while creating the column\'), nuTranslate(\'The query contains invalid keywords\')]);\";\n\n}\n\nnuJavascriptCallback($js);\n', 'hide', '', '1', '0', '', NULL),
