('nu61d8283e98e8b2e', 'NUCHECKPASSWORDPOLICY_TEMPLATE', 'Rules for enforcing Password policies', 'nubuilder', '\nfunction nuFATick($nok) {\n\n    $icon = $nok == false ? \"far fa-check-circle\" : \"far fa-times-circle\";\n    $color = $nok == false ? \"green\" : \"red\";\n\n    return \"&nbsp;&nbsp;<i class=\'$icon\' style=\'color:$color\'></i>&nbsp;&nbsp;\";\n\n}\n\nfunction nuCheckPasswordPolicy() {\n\n    $oldpw = \'#old_password#\';\n    $newpw = \'#new_password#\';\n\n    $atLeast = nuTranslate(\"Your New Password must contain at least\").\":\";\n    $msg = \'\';\n    $c = 0;\n\n    $check = strlen($newpw) < 8;\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"8 Characters\") . \"<br>\";\n\n    $check = !preg_match(\"#[0-9]+#\", $newpw);\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"1 Number\") . \"<br>\";\n\n    $check = !preg_match(\"#[A-Z]+#\", $newpw);\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"1 Capital Letter\") . \"<br>\";\n\n    $check = !preg_match(\"#[a-z]+#\", $newpw);\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"1 Lowercase Letter\") . \"<br>\";\n\n    $check = !preg_match(\'/[\\\'\\/~`\\!@#\\$%\\^&\\*\\(\\)_\\-\\+=\\{\\}\\[\\]\\|;:\"\\<\\>,\\.\\?\\\\\\]/\', $newpw);\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"1 Special Character\") . \"<br>\";\n\n    if ($msg != $atLeast) $msg = $atLeast . \"<br><br>\" . $msg;\n\n    if ($newpw === $oldpw) {\n        $msg .= \"<br>\" . nuFATick(true) . nuTranslate(\"The provided New Password cannot be the same as the Current Password\") . \"<br>\";\n        $c++;\n    }\n\n    if ($c > 0) {\n        nuDisplayError($msg);\n        return false;\n    }\n    else {\n        return true;\n    }\n\n}\n\n$check = nuCheckPasswordPolicy();\n\n', 'hide', '', '0', '1', '', NULL),
('nu61eceada0768c21', 'NUCHANGEPASSWORD', 'Change user password', 'nubuilder', '$is = \'#old_password#\';\n$was = \'#new_password_check#\';\n$will = \'#new_password#\';\n$ses = \'#SESSION_ID#\';\n$userId = \"#USER_ID#\";\n\n//--\n\n$s = \"\n SELECT sss_access \n FROM zzzzsys_session\n WHERE zzzzsys_session_id = ?\n\";\n\n$t = nuRunQuery($s, array($ses));\n$r = db_fetch_object($t);\n$j = json_decode($r->sss_access);\n\n//--\n\n$s = \"\n SELECT sus_login_password\n FROM zzzzsys_user\n WHERE zzzzsys_user_id = ?\n\";\n\n$t = nuRunQuery($s, array($userId));\n$r = db_fetch_object($t);\n$current = $r->sus_login_password;\n\n$useMd5 = $_SESSION[\'nubuilder_session_data\'][\'USE_MD5_PASSWORD_HASH\'] == true;\n\nif ($useMd5) {\n $check = md5($is) == $current;\n} else {\n $check = password_verify($is, $current);\n}\n\nif ($check == false) {\n nuDisplayError(nuTranslate(\'Incorrect Password\'));\n return;\n} \n\n//--\n\n$p = nuProcedure(\'nuCheckPasswordPolicy\');\n\nif($p != \'\'){\n\n eval($p);\n \n if($check == false){\n return;\n }\n\n}\n\n//--\n\nif($will == $was){\n\n $s = \"\n\n UPDATE zzzzsys_user \n SET sus_login_password = ?\n WHERE zzzzsys_user_id = ?\n \n \";\n\n $pwHash = $useMd5 == true ? md5($will) : nuPasswordHash($will);\n\n nuRunQuery($s, array($pwHash, $userId));\n\n nuSetUserJSONData(\'PASSWORD_CHANGED_TIME\', time() , $userId);\n nuSetUserJSONData(\'PASSWORD_CHANGED_SOURCE\', \'user\', $userId);\n \n $js = \"nuMessage(nuTranslate(\'Your password has been successfully changed\'));\";\n \n}else {\n $msg = nuTranslate(\'\"New Password\" must be the same as \"Confirm New Password\"\');\n $js = \"nuMessage($msg);\";\n}\n\n\nnuJavascriptCallback($js);\n\n', 'hide', '', '1', '1', '', NULL),
